.bannerArea{
    position: relative;
}
.bannerArea:before{
    position: absolute;
    content: '';
    width: 300px;
    height: 300px;
    background: url('http://megafxsignals.com/new-staging/wp-content/uploads/2021/04/dot-sha.png');
    border-radius: 50%;
    right: -5%;
    top:-2%;
    opacity: .4
}

.bannerArea:after{
    position: absolute;
    content: '';
    width: 300px;
    height: 300px;
    background: url('http://megafxsignals.com/new-staging/wp-content/uploads/2021/04/dot-sha.png');
    border-radius: 50%;
    left: -5%;
    top:50%;
    transform: translateY(-50%);
    opacity: .4
}
@media(max-width:575px){
    .bannerArea:before{
        width: 100px;
        height: 100px;
        top:10%;
    }

    .bannerArea:after{
        width: 150px;
        height: 150px;
        left: -5%;
        top:50%;
    }
}

